$(document).ready(function(){$(".jsFade").hide();$(".jsFade").each(function(){var e=Math.floor(Math.random()*111+1);$(this).fadeIn(e*10)})});$(document).ready(function(){$(".jsSteadyFade").hide();$(".jsSteadyFade").each(function(){$(this).fadeIn(999)})});$(document).ready(function(){$(".latestAction").hide();$(".latestAction").each(function(){$(this).fadeIn(999)})});$(document).ready(function(){$(".hiddenToggle").hide();$(".toggle").click(function(){var e="#"+$(this).attr("data-mark");$(e).slideToggle(75,"linear")})});$(document).ready(function(){$(".profileTag").click(function(){var e=$(this).attr("data-id");var t="#"+e+"_profile_new_tags";var n=$(t).find("input");var r=n.val();var i="";if(r.length==0){i=r+$(this).attr("data-tag")}else{if(r.substring(r.length-2,r.length)!=", "){i=r+", "+$(this).attr("data-tag")}else{i=r+$(this).attr("data-tag")}}n.val(i)})});$(document).ready(function(){$(".postTag").click(function(){var e=$(this).attr("data-flag");var t="#"+e+"_post_new_tags";var n=$(t).find("input");var r=n.val();var i="";if(r.length==0){i=r+$(this).attr("data-tag")}else{if(r.substring(r.length-2,r.length)!=", "){i=r+", "+$(this).attr("data-tag")}else{i=r+$(this).attr("data-tag")}}n.val(i)})});$(function(){$("form").each(function(){$(this).find("input").keypress(function(e){if(e.which==10||e.which==13){this.form.submit()}})})})